select a.book_id,a.title,a.author,a.genre,a.publication_year, count(b.record_id) as current_borrowers from library_books a left join borrowing_records b on a.book_id = b.book_id where b.return_date is NULL  group by a.book_id,a.title,a.author,a.genre,a.publication_year,a.total_copies having  a.total_copies = COUNT(b.record_id) order by current_borrowers desc,a.title asc;